INSERT INTO `tours` (`id`, `Tour`, `Description`, `Include`, `NotInclude`, `Itinerary`, `Frequency`, `Recommendation`, `AdultPrice`, `KidPrice`, `Map`, `Youtube`, `url`, `Supplier`, `Thumbnail`, `Location`, `ListPrice`, `Kid`, `Type`, `title`, `metakeywords`, `metadescription`, `active`) VALUES
(1, 'Jungle Tour Adventure - Speed Boat', '&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p&gt;On a fabulous &lt;strong&gt;Speed Boat &lt;/strong&gt;explore the emerald green &lt;strong&gt;Nichupt&amp;eacute; Lagoon &lt;/strong&gt;and enter at the amazing mangrove, also snorkel 45 min. at the Nizuc reef.&lt;/p&gt;\n&lt;p&gt;This experience is amazing you can enjoy 2 activities in the same place and time.&lt;/p&gt;\n&lt;p&gt;&lt;span&gt;Start the most exciting &amp;ldquo;Jungle Tour&amp;rdquo; feeling the breeze on your face while driving your own two-seater speedboat through the lagoon, channels and mangroves until you arrive at a beautiful coral reef offshore. Tie your boat up and jump into the Caribbean waters where you&amp;rsquo;ll snorkel among the most colorful tropical fish. Visit one of the most beautiful coral reef and get amazed with its diverse ferns and sponges.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;color: #333333; font-family: &#039;Trebuchet MS&#039;, Arial, Helvetica, sans-serif; font-size: 12px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;', '&lt;ul&gt;\r\n&lt;li&gt;1 bottle water&lt;/li&gt;\r\n&lt;li&gt;Life Jacket&lt;/li&gt;\r\n&lt;li&gt;Equipment (snorkel tube is yours to keep)&lt;/li&gt;\r\n&lt;/ul&gt;', 'US$3 Federal Marine Park and dock fee payable upon check in.\r\n\r\nGround Transportation (please call us and ask for a quotation)', '&lt;ul&gt;\r\n&lt;li&gt;Register&lt;/li&gt;\r\n&lt;li&gt;Briefing and Instruction&lt;/li&gt;\r\n&lt;li&gt;Discover the Mangrove&lt;/li&gt;\r\n&lt;li&gt;Snorkel&lt;/li&gt;\r\n&lt;li&gt;Have fun!&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 'DAILY', '&lt;p&gt;&lt;strong&gt;Meeting point&lt;/strong&gt;: Boulevard Kukulc&amp;aacute;n Km 6.5 Int.Marina Aqua Tours. Canc&amp;uacute;n Hotel Zone&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Duration&lt;/strong&gt;: 2 hours and a half&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Minimum driver age&lt;/strong&gt;: 18 years&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Minimum Passenger age&lt;/strong&gt;: 10 years&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;**Do not allow pregnant Women&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;we suggest to bring:&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;swim suit&lt;/p&gt;\r\n&lt;p&gt;Biodegradable sun block&lt;/p&gt;\r\n&lt;p&gt;Towel&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '69.00', '49.00', NULL, NULL, 'jungle-tour-speed-boat', 3, 'images/pictures/113546.png', 1, NULL, '0', '1', 'Jungle Tour - Speed Boat | cancun water activities | boat tour			', 'jungle tour cancun,cancun water activities,jungle tour cancun,cancun jungle tour,speed boat tour,speedboat tours,speed boat rides,tours cancun,jungle tours,horse back riding in cancun.', ' Jungle Tour speedboat for 45 minutes, James Bond style, across the Nichupte Lagoon through the twisting mangrove channels in to the Mexican Caribbean.', '1'),
(2, 'Fishing in Cancun', '&lt;p&gt;Cancun has been named in &lt;strong&gt;the top 20 places for billfishing &lt;/strong&gt;and also provides excellent game for light tackle.&lt;/p&gt;\r\n&lt;p&gt;Charter a half or full day fishing boat with us and our bilingual captains will make sure you have a &lt;strong&gt;great time&lt;/strong&gt;.&lt;/p&gt;\r\n&lt;p&gt;The Captains will take you to the &lt;strong&gt;prime spots &lt;/strong&gt;to catch &lt;strong&gt;Sailfish&lt;/strong&gt;, &lt;strong&gt;Marlin&lt;/strong&gt;, &lt;strong&gt;Bonito&lt;/strong&gt;, &lt;strong&gt;Wahoo&lt;/strong&gt;, &lt;strong&gt;Barracuda&lt;/strong&gt;, &lt;strong&gt;Grouper&lt;/strong&gt;... the list is endless.&lt;/p&gt;\r\n&lt;p&gt;We offers &lt;strong&gt;private&lt;/strong&gt; and &lt;strong&gt;shared&lt;/strong&gt; options so we can cater for your group or individuals.&lt;/p&gt;\r\n&lt;p&gt;There are plenty of beverages available consisting of water, sodas and beers.&lt;/p&gt;\r\n&lt;p&gt;Catch that evening&amp;acute;s dinner and the crew will be onhand to &lt;strong&gt;prepare it for you&lt;/strong&gt;!&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '&lt;ul&gt;\r\n&lt;li&gt;Beverage (Water, Sodas and beers)&lt;/li&gt;\r\n&lt;li&gt;Equipment&lt;/li&gt;\r\n&lt;li&gt;Bilingual Captain&lt;/li&gt;\r\n&lt;li&gt;Crew&lt;/li&gt;\r\n&lt;/ul&gt;', 'US$3 Dock Fee Payable upon check-in p/p\r\n \r\nGround Transportation (please call us and ask for a quotation)', '&lt;ul&gt;\r\n&lt;li&gt;Register&lt;/li&gt;\r\n&lt;li&gt;Briefing and Instruction&lt;/li&gt;\r\n&lt;li&gt;Start the adventure&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 'MONDAY TO  SATURDAY', '&lt;p&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;Meeting Point: Boulevard Kukulcan Km.15.2 interior Marine AquaWorld&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Departures&lt;/strong&gt;: at 7 am or 1 pm&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Shared Charters&lt;/strong&gt;: Minimum 2 pax to depart, maximum 6&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Private Chartes&lt;/strong&gt;: Maximum 8 pax&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Durations&lt;/strong&gt;: 4,6,8 or 12 hours&lt;/p&gt;\r\n&lt;p&gt;Once you have arrangements, all you need to do is make your way to the marina Aquaworlk which is very conveniently located and easy to find.&lt;/p&gt;\r\n&lt;p&gt;Check in at the Marina&amp;acute;s reception where you will be given your boarding pass sent to meet your captain and the crew of your cancun fishing charter.&lt;/p&gt;\r\n&lt;p&gt;Please Bring:&lt;/p&gt;\r\n&lt;ul&gt;\r\n&lt;li&gt;Comfortable clothe&lt;/li&gt;\r\n&lt;li&gt;sunblock&lt;/li&gt;\r\n&lt;li&gt;camera&lt;/li&gt;\r\n&lt;li&gt;hat&lt;/li&gt;\r\n&lt;li&gt;sun glasses&lt;/li&gt;\r\n&lt;li&gt;extra cash, for tips, photo and video, souvenirs&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p&gt;we recommend have a &lt;strong&gt;light breakfast &lt;/strong&gt;before your Trip.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; and..... &lt;strong&gt;GOOD FISHING!!!!!!!!&lt;/strong&gt;&lt;/p&gt;', '0.00', '0.00', NULL, NULL, 'fishing', 2, 'images/pictures/0rfadwpminifishing.jpg', 1, '&lt;p&gt;Shared: US$139 P/P&lt;/p&gt;\r\n&lt;p&gt;Private 4 hrs: US$556&lt;/p&gt;\r\n&lt;p&gt;Private 6 hrs: US$667&lt;/p&gt;\r\n&lt;p&gt;Private 8 hrs: US$778&lt;/p&gt;\r\n&lt;p&gt;Private 10 hrs: US$999&lt;/p&gt;', '0', '3', 'fishing in cancun  | fishing tours | fishing adventures', 'fishing in cancun,cancun attractions,cancun sea,cancun fishing,fishing cancun,fishing adventures,vacations fishing,fishing tours,travel cancun,tours to yucatan.', 'In the exciting world of fishing,the mexican caribbean offers an adventure. you will find s,mahi-mamahis,baracudas,tunas,groupers,sharks and many other fishes', '1'),
(3, 'Dolphin Royal Swim All Inclusive', '&lt;p&gt;This is, undoubtedly, the most famos of Dolphin&amp;acute;s Programs and can be summarized in two words: &lt;strong&gt;ACTION AND SPEED&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;Two Dolphins &lt;/strong&gt;welcome you with an affectionate &lt;strong&gt;handshake&lt;/strong&gt;, a &lt;strong&gt;kiss&lt;/strong&gt; on the check, and let you give them one back. Later, they take you for a &lt;strong&gt;Speedy ride &lt;/strong&gt;while you hold on to their fins.&lt;/p&gt;\n&lt;p&gt;The most thrilling moment of this program is during the &lt;strong&gt;FOOT-PUSH&lt;/strong&gt;, when you feel all the strenght of your new friends while they push you across the water from the bottom of your feet.&lt;/p&gt;\n&lt;p&gt;it is truly.... the &lt;strong&gt;EXPERIENCE OF A LIFETIME!!&lt;/strong&gt;&lt;/p&gt;', '&lt;ul&gt;\r\n&lt;li&gt;From Cancun to Isla Round Trip Ferry &lt;/li&gt;\r\n&lt;li&gt;From Riviera Maya to Pto.Aventuras included Ground Transportation Round Trip&lt;/li&gt;\r\n&lt;li&gt;Dolphin Royal Swim (kiss, hug, hand target, pictures with ball, foot push, dorsal, bar jump, clock game, peck shate, 10 minutes free time)&lt;/li&gt;\r\n&lt;li&gt;8 guest per group to swimm&amp;nbsp; &lt;/li&gt;\r\n&lt;li&gt;Promotion: snorkel with stingrays or cat shark and a Family Picture with Sea Lions&lt;/li&gt;\r\n&lt;li&gt;Lunch Combo&lt;/li&gt;\r\n&lt;li&gt;Beverage (soda or water)&lt;/li&gt;\r\n&lt;li&gt;visit to Isla Mujeres Down Town &lt;/li&gt;\r\n&lt;li&gt;watch sharks and stingrays&lt;/li&gt;\r\n&lt;li&gt;bike tour &lt;/li&gt;\r\n&lt;/ul&gt;', ' US$5 p/p Dock Fee\r\n \r\n Photo and Video\r\n\r\n Tips and Souvenirs\r\n\r\n Turtle Farm Visit($30 pesos)', '&lt;ul&gt;\r\n&lt;li&gt;Register&lt;/li&gt;\r\n&lt;li&gt;Ferry Leave at 9 am&lt;/li&gt;\r\n&lt;li&gt;aprox 30 min to Isla Mujeres&lt;/li&gt;\r\n&lt;li&gt;Briefing and Instructions&lt;/li&gt;\r\n&lt;li&gt;50 min Royal Swim&amp;nbsp;&lt;/li&gt;\r\n&lt;li&gt;Free time&lt;/li&gt